    private void FanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FanActionPerformed
        // TODO add your handling code here:
    FAN=FAN+1;
    int minfan=1;
    if(FAN>=6){
        FAN=5;
        fanLabel.setText("5");
        FAN=minfan;
        fanLabel.setText(""+FAN);
    }
    else{
        fanLabel.setText(""+FAN);
    }
    
    }//GEN-LAST:event_FanActionPerformed

    private void SWGActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SWGActionPerformed
        // TODO add your handling code here:
        if(onoff==0){
            onoff=1;
            swingLabel.setText("ON");
        }
        else if(onoff==1){
            onoff=0;
            swingLabel.setText("OFF");
        }
    }//GEN-LAST:event_SWGActionPerformed

