    private void OnOffMouseReleased(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_OnOffMouseReleased
        // TODO add your handling code here:
        if(onoff==0){
            onoff = 1;
            onof.setText("ON");
        }else{
            onoff = 0;
            onof.setText("OFF");
        }
    }//GEN-LAST:event_OnOffMouseReleased

    private void plusMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_plusMouseClicked
        // TODO add your handling code here:
        if(onoff==1 && suhu<32){
            suhu++;
            suh.setText(""+suhu);
        }
    }//GEN-LAST:event_plusMouseClicked

    private void minusMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_minusMouseClicked
        // TODO add your handling code here:
        if(onoff==1 && suhu>16){
            suhu--;
            suh.setText(""+suhu);
        }
    }//GEN-LAST:event_minusMouseClicked

    private void PwrChldActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PwrChldActionPerformed
        // TODO add your handling code here:
        suh.setText(""+suhu);
    }//GEN-LAST:event_PwrChldActionPerformed

    private void ModeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ModeActionPerformed
        // TODO add your handling code here:
        if(suhu>=16&&suhu<=20){
           suh.setText(""+suhu);
           dinginLabel.setText("Dingin");
        }
        else if(suhu>=26&&suhu<=32){
           suh.setText(""+suhumax);
           dinginLabel.setText("Panas");
        }
        else if(suhu>=21&&suhu<=25){
            suh.setText(""+sh);
            dinginLabel.setText("Normal");
        }
    }//GEN-LAST:event_ModeActionPerformed

